values.Add("Salary", 2000);
"select Test.Application.Employee where this.Name = ? and this.Salary > ?"

values.Add("Name", "Paul Jones");
values.Add("Salary", 2000);
"select Test.Application.Employee where Not(this.Name = 'Paul Jones' and this.Salary > 2000)"

